    <script>
        function printDiv(branch_slog) {
            var a = window.open('', '', 'height=500, width=500');
            var print_img_header2 = $("<img>", {
                src: 'https://qrcode.tec-it.com/API/QRCode?data=' +
                window.location.origin + '/id/shop/' + branch_slog ,
                alt: "Image description",
                class: 'print_img',
                id:'print_img_header2'

            });

            var print_img_header1 = $("#print_img_header1").clone();
            var print_img_header22 = $("#print_img_header22").clone();
            var print_img_header3 = $("#print_img_header3").clone();
            print_img_header1.css("display","block");
            print_img_header22.css("display","block");
            print_img_header3.css("display","block");
            var parent = $("<div id='parent'></div>");
            var center = $('<center></center>')
            var card_qr = $('<div id="card_qr" class="mb-3" ></div>')

            parent.append(print_img_header1)
            parent.append(print_img_header22)
            parent.append(print_img_header2)
            parent.append(print_img_header3)

            center.append(parent)
            card_qr.append(center)
            var divContents = card_qr.html();
            a.document.write('<html><style> html, body { width: 55mm; height: 9mm; } </style><style> .print_img { width:100%; } </style>');
            a.document.write('<style> #print_img_header2{ height: 35mm;width: 35mm;} </style>')
            a.document.write('<body style="display: flex;flex-direction: column;box-sizing: border-box;padding-top:0px;border:1px solid black;margin :0" >');
            a.document.write(divContents);
            a.document.write('</body></html>');
            a.document.close();
            setTimeout(() => {
                a.print();
            }, 1000);
        }
        function editBranch(branchId)
        {
            const branchesDOM = document.getElementById("branches");
            const branches = JSON.parse(branchesDOM.dataset.obj);
            const branch = branches.find(b => b.id == branchId);
            if(branch){
                $('#edited_branch_id').val(branch.id);
                $('#edited_branch_name').val(branch.name);
                $('#edited_branch_name_ar').val(branch.name_ar);
                $('#edited_branch_address').val(branch.address);
                $('#edited_branch_address_ar').val(branch.address_ar);
                $('#edited_branch_website').val(branch.website);
                $('#edited_branch_latitude').val(branch.latitude);
                $('#edited_branch_longitude').val(branch.longitude);
                var url = `${window.location.origin}/edit_branch/${branch.id}`;
                $('#edited_branch_form').attr("action", url);
                $('#edited_branch_city').val(branch.city_id);
                $('#edit_branch').modal('show', {backdrop: 'static'});
            }else{
                console.log(`Branch with Id ${branchId} not found`);
            }

        }
    </script>

